import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import ValueIcon from '@material-ui/icons/Apps';
import OrderValue from './OrderValueComponent';

const styles = theme => ({
    settings:{},
    values: {
	display: 'inline-block',
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,valueitem,valueindex) {
    return (<MenuItem value={valueitem}>
	    <OrderValue classes={classes} state={state} keyitem={keyitem} valueitem={valueitem}/> 
	    </MenuItem>);
}
class Values extends Component {
    state={anchor:null};
    handleClick = event => {this.setState({ anchor: event.currentTarget });};
    handleClose = () => {this.setState({ anchor: null });};
    render() {
        const { classes, state, keyitem, keyindex } = this.props;
	var items=state.Path.getOrderValues(state,keyitem);
	var mapFunction= (item,index)=>renderMenuItem(classes,state,keyitem,item,index);
	//console.log("Values.rendering",items.length,JSON.stringify(anchor),Boolean(anchor));
	return (
		<div className={classes.values}>
		   <Button
                      aria-owns={this.state.anchor ? 'values-menu' : undefined}
                      aria-haspopup="true"
                      onClick={this.handleClick}
		    >
	  	{<ValueIcon state={state}/>} {keyitem}
                     </Button>
		     <Menu
                        id="values-menu"
	                anchorEl={this.state.anchor}
                        open={Boolean(this.state.anchor)}
                        onClose={this.handleClose}
		     >
		        {items.map(mapFunction)}
	             </Menu>
		</div>
	);
    }
}

Values.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Values);
