import React, { Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    pointer: {
	cursor:"pointer",
	padding: theme.spacing.unit*0,
    },
    nopointer: {
	padding: theme.spacing.unit*0,
    },
});

function getTextWidth(txt, fontname, fontsize){
    if(getTextWidth.c === undefined){
        getTextWidth.c=document.createElement('canvas');
        getTextWidth.ctx=getTextWidth.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextWidth.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextWidth.ctx.measureText(txt).width;
};
function getTextHeight(fontname, fontsize){
    if(getTextHeight.c === undefined){
        getTextHeight.c=document.createElement('canvas');
        getTextHeight.ctx=getTextHeight.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextHeight.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextHeight.ctx.measureText('M').width;
}
function updateCanvas(item) {
    const {state,label,rotate} = item.props;
    const cnv=item.refs.text;
    const ctx = cnv.getContext('2d');
    //var cnvheight = cnv.height;
    ctx.save();
    //ctx.translate(newx, newy);
    //ctx.font = "40px Courier"
    ctx.textAlign = "left"; //left right center
    if (rotate !== undefined) {
	ctx.rotate(-Math.PI/2);
	console.log("Pos:",item.width,item.height);
	ctx.fillText(label, item.width/2, item.height/2); // labelXposition
    } else {
	ctx.fillText(label, 0, item.height/2); // labelXposition
    };
    ctx.restore();
    ctx.rect(0,0,item.width,item.height);
    ctx.stroke();
    console.log("Label:",label,item.width,item.height);
    //console.log("Text:",cnv.width,cnv.height);
}


class TextComponent extends React.Component {
    constructor(props) {
        super(props);
        const {state} = props;
    };
    componentDidMount() {
        updateCanvas(this);
    }
    render() {
        const { classes, onclick, title, label, rotate, ...other } = this.props;
	var cursor=classes.nopointer;
	if (onclick !== undefined) {
	    cursor=classes.pointer;
	}
	this.width=getTextWidth(label);
	this.height=getTextHeight();
	if (rotate !== undefined) { // rotate width/height
	    var buff=this.width;
	    this.width=this.height;
	    this.height=buff;
	}
        return (
		<canvas {...other} className={cursor} classes={classes} onClick={onclick} title={title} ref="text" width={this.width} height={this.height}/>
        );
    }
}

TextComponent.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(TextComponent);
