//console.log("Loading DefaultLib.js");
function Default() {
    this.deffile="defaults.json"; // defaults file, contains default setup...
    this.other=undefined;
    this.trash=undefined;
    this.init=function(state){
	var par="Default";
	state.Utils.init(par,this);
    };
    this.loadDefault=function(state, callbacks ) {
	//console.log("loadDefault");
	if (state.Threshold.thrs !== undefined) { // defaults already loaded, execute callback...
	    var callback=callbacks.shift();
	    if (callback !== undefined) {
		setTimeout(callback(state,callbacks),0);
	    }
	} else {
	    if (state.Database.ready) { // make sure we do not re-load if we are already loading...
		state.Database.ready=false;
		state.Html.setFootnote(state, "Server-request: "+this.deffile,"secondary");
		var defHttp = new XMLHttpRequest();
		defHttp.addEventListener("progress",state.Html.progressInfo);
		defHttp.addEventListener("load",state.Html.loadInfo);
		defHttp.addEventListener("error",state.Html.errorInfo);
		defHttp.addEventListener("abort",state.Html.abortInfo);
		defHttp.onreadystatechange = function() {
		    state.Database.ready=true;
		    if (defHttp.readyState  === 4) {
			if (defHttp.status  === 200) {
			    console.log(defHttp.responseText);
			    var json = JSON.parse(defHttp.responseText);
			    if (state.Default.other  === undefined) {state.Default.other=json.other;}
			    if (state.Default.trash  === undefined) {state.Default.trash=json.trash;}
			    state.Default.Threshold={thrs:json.thrs};
			    state.Default.Colors={colors:json.colors};
			    state.Default.Path={order:json.order,select:json.select};
			    state.Default.Show={state:json.state};
			    state.Default.Layout={priority:json.priority};
			    state.Default.pushDefault(state)
			    var callback=callbacks.shift();
			    if (callback !== undefined) {
				setTimeout(callback(state,callbacks),0);
			    }
			} else if (state.React !== undefined && state.React.Html !== undefined) {
			    state.React.Html.setFootnote(state,"Unable to load "+this.deffile,"");
			}
		    }
		}
		defHttp.responseType="";
		defHttp.overrideMimeType("text/text");
		defHttp.open("GET", "def/"+this.deffile, true);
		defHttp.setRequestHeader('cache-control', 'no-cache, must-revalidate, post-check=0, pre-check=0');
		defHttp.setRequestHeader('cache-control', 'max-age=0');
		defHttp.setRequestHeader('expires', '0');
		defHttp.setRequestHeader('expires', 'Tue, 01 Jan 1980 1:00:00 GMT');
		defHttp.setRequestHeader('pragma', 'no-cache');
		defHttp.send(); 
	    } else {
		state.Html.setFootnote(state,"Already waiting for reply...");
	    };
	};
    };
    this.pushDefault=function(state) {
	if (state.Threshold.thrs  === undefined) {state.Threshold.thrs=state.Default.Threshold.thrs;}
	if (state.Colors.colors  === undefined) {state.Colors.colors=state.Default.Colors.colors;}
	if (state.Path.order  === undefined) {state.Path.order=state.Default.Path.order;}
	if (state.Path.filter  === undefined) {state.Path.filter=state.Default.Path.filter;}
	if (state.Path.select  === undefined) {state.Path.select=state.Default.Path.select;}
	if (state.Show.state  === undefined) {state.Show.state=state.Default.Show.state;}
	if (state.Layout.priority  === undefined) {state.Layout.priority=state.Default.Layout.priority;}
	// console.log("Default thrs:",JSON.stringify(state.Threshold.thrs));
	// console.log("Default colors:",JSON.stringify(Colors));
	//console.log("Default other:",JSON.stringify(state.Default.other));
	//console.log("Default trash:",JSON.stringify(state.Default.trash));
	//console.log("Default order:",JSON.stringify(state.Path.order));
	//console.log("Done loading:",JSON.stringify(state.Default.trash));
    };
    this.hasChanged=function(state,main,item) {
	if (state.Default!==undefined && 
	    state.Default[main]!==undefined &&
	    state.Default[main][item]!==undefined) {
	    var src=JSON.stringify(state[main][item]);
	    var trg=JSON.stringify(state.Default[main][item]);
	    //console.log("Checking:",main,item,"\n",src,"\n",trg);
	    return src!==trg;
	} else {
	    return true;
	}
    };
};
export default Default;
