    for (var kk = slen - 1; kk >= 0; kk--) {
      key = keys[kk];

      if (dependancy[key] === "common") {
        // leave at least one redundant variable
        cnt = cnt + 1;

        if (cnt < slen) {
          information[key] = "redundant";
        }
      }
    }

    ;
    return information;
  };

  this.getNextTableCandidate = function (state, where, key, cand) {
    var clen = cand.length;

    for (var cc = 0; cc < clen; cc++) {
      var ckey = cand[cc];
      var table = [key, ckey];
      var dependancy = this.compareKeys(state, where, table);
      var information = this.getInformation(state, table, dependancy);

      if (information[ckey] === undefined) {
        cand.splice(cc, 1);
        return ckey;
      }
    }

    return; // no cand-idate
  };

  this.disposeTableKey = function (state, key, dependancy, information) {
    var bdone;

    if (information[key] === "redundant") {
      // trash key
      bdone = false;
      state.Utils.moveToArray(state.Path.keys.other, state.Path.keys.path, key, -1);
      var val = dependancy.val[key];
      state.Path.select.val[key] = [val];
      state.Path.where[key] = key + '="' + val + '"'; //this.order[key]=order;
    } else if (information[key] === "insignificant") {
      // trash key
      bdone = false;
      state.Utils.moveToArray(state.Path.keys.other, state.Path.trash, key, 0);
    }

    ;
    return bdone;
  };

  this.checkTableKeys = function (state) {
    var ckey;
    var where = state.Database.getWhere(state);
    state.Matrix.initKeyCnt(state);
    state.Matrix.mapKeyCnt(state, where, 0, state.Path.keys.other); // possible table keys (no undefined)

    state.Path.exportAllKeys(state);
    console.log("checkTableKeys Starting:", JSON.stringify(state.Path.keys), JSON.stringify(state.Path.trash), JSON.stringify(state.Path.other.table));
    var table = state.Path.other.table; // current table-keys

    var cand = state.Path.other.rest; // current candidates

    var bdone = table.length < 2;

    do {
      //check current table-keys
      var dependancy = this.compareKeys(state, where, table);
      var information = this.getInformation(state, table, dependancy); // check if first key is invalid

      var fkey = table[0];
      var skey = table[1];
      var fdest = information[fkey];
      var sdest = information[skey];
      console.log("Looping:", fkey, dependancy.dep[fkey], fdest, skey, dependancy.dep[skey], sdest);

      if (sdest !== undefined) {
        // second key is in-valid
        // get next candidate
        ckey = this.getNextTableCandidate(state, where, fkey, cand);

        if (ckey !== undefined) {
          console.log("Found 2-candidate:", fkey, ckey, " removing ", skey);
          this.disposeTableKey(state, skey, dependancy, information);
          table = [fkey, ckey];
          bdone = false;
        } else {
          // no more candidates
          console.log("No 2-candidate:", fkey, JSON.stringify(cand));
          bdone = true;
        }
      } else if (fdest !== undefined) {
        // first key is in-valid
        // get next candidate
        ckey = this.getNextTableCandidate(state, where, skey, cand);

        if (ckey !== undefined) {
          console.log("Found 1-candidate:", skey, ckey, " removing ", fkey);
          this.disposeTableKey(state, fkey, dependancy, information);
          table = [skey, ckey];
          bdone = false;
        } else {
          // no more candidates
          console.log("No 1-candidate:", skey, JSON.stringify(cand));
          bdone = true;
        }
      } else {
        // both keys are valid
        console.log("Keys ok:", fkey, skey);
        bdone = true;
      }

      if (!bdone) {
        state.Path.exportAllKeys(state);
      }
    } while (!bdone);

    console.log("checkTableKeys Done:", JSON.stringify(state.Path.keys), JSON.stringify(state.Path.trash));
  };

  this.checkTableKeysOld = function (state) {
    var ret = [];
    var where = state.Database.getWhere(state);
    state.Matrix.initKeyCnt(state);
    state.Matrix.mapKeyCnt(state, where, 0, state.Path.keys.other); // possible table keys (no undefined)

    state.Path.exportAllKeys(state);
    console.log("this.checkTableKeys Entering.", JSON.stringify(state.Path.keys)); //var keys=state.Path.other.table;

    var bdone = state.Path.other.table.length > 1;
    var order = state.Utils.cp(state.Path.keys.other);

    do {
      console.log("this.checkTableKeys Looping.", state.Path.other.rest.length);
      bdone = true;

      if (state.Path.other.table.length > 1) {
        // check for number of column values
        var dependancy = this.compareKeys(state, where, state.Path.other.table);
        var information = this.getInformation(state, state.Path.other.table, dependancy);
        console.log("Rm:", JSON.stringify(dependancy), JSON.stringify(information));
        var slen = state.Path.other.table.length;

        for (var jj = 0; jj < slen; jj++) {
          var key = state.Path.other.table[jj];

          if (information[key] === "redundant") {
            // move key to path
            bdone = false;
            state.Utils.moveToArray(state.Path.keys.other, state.Path.keys.path, key, -1);
            var val = dependancy.val[key];
            state.Path.select.val[key] = [val];
            state.Path.where[key] = key + '="' + val + '"';
            this.order[key] = order;
          } else if (information[key] === "insignificant") {
            // trash key
            bdone = false;
            state.Utils.moveToArray(state.Path.keys.other, state.Path.trash, key, 0);
          }

          ;
        }

        if (!bdone) {
          state.Path.exportAllKeys(state);
        }
      }

      ;
    } while (!bdone); //console.log("this.checkTableKeys Done.",JSON.stringify(state.Path.other.table));


    return ret;
  }; // get X-priority, {colx,coly1,coly2...)


  this.sortTableKeys = function (state) {
    // make priority index
    var arr = state.Utils.cp(this.other.table);
    var pri = state.Layout.getPriorityIndex(state, arr); // sort the array according to priority

    this.other.table = arr.sort(function (a, b) {
      if (a === "") {
        return 1;
      } else if (b === "") {
        return -1;
      } else if (pri[a] < pri[b]) {
        return 1;
      } else if (pri[a] > pri[b]) {
        return -1;
      } else {
        return 0;
      }
    }); //console.log("TableKeys:",JSON.stringify(this.other.table),
    //	" Pri:",JSON.stringify(state.Layout.priority),
    //	" P:",JSON.stringify(pri));

    return;
  };

  this.keyVal = function (state, key) {
    if (this.select.val[key] !== undefined && this.select.val[key] !== null && this.select.val[key] !== "") {
      return this.select.val[key];
    } else {
      return;
    }
  };

  this.keyValOk = function (state, val) {
    var ok = true;
    return ok;
  };

  this.moveKey = function (state, styp, skey, ttyp, tid) {
    var sid = this.keys[styp].indexOf(skey);

    if (sid !== -1) {
      var src = this.keys[styp].splice(sid, 1); // remove from path

      if (tid === undefined || tid === -1) {
        this.keys[ttyp] = this.keys[ttyp].concat(src); // last position
      } else {
        state.Utils.spliceArray(this.keys[ttyp], tid, 0, src);
      }

      return true;
    } else {
      return false;
    }
  };

  this.moveKeys = function (state, styp, skeys, ttyp, tid) {
    var len = skeys.length;

    for (var ss = 0; ss < len; ss++) {
      var skey = skeys[ss];
      var sid = this.keys[styp].indexOf(skey);

      if (sid !== -1) {
        var src = this.keys[styp].splice(sid, 1); // remove from path

        if (tid === undefined || tid === -1) {
          this.keys[ttyp] = this.keys[ttyp].concat(src); // last position
        } else {
          state.Utils.spliceArray(this.keys[ttyp], tid, 0, src);
        }
      }
    }
  };

  this.swapTableKey = function (state, skey, tkey) {
    var sid = this.other.table.indexOf(skey);
    var tid = this.other.table.indexOf(tkey);

    if (tid !== sid && this.other.table.length > 1) {
      var tin2 = this.keys.other.indexOf(this.other.table[1]);
      var tkey2 = this.keys.other.splice(tin2, 1); // remove from array

      state.Utils.spliceArray(this.keys.other, 0, 0, tkey2); //console.log("Swapped:",tkey2,tkey,JSON.stringify(this.keys.other));

      return true;
    } else {
      return false;
    }
  };

  this.delTableKey = function (state, key) {
    var secondkey;
    var secondind;
    var sid = this.keys.other.indexOf(key);

    if (sid !== -1) {
      var sin = this.other.table.indexOf(key);
      var save = sin === 0;

      if (save) {
        secondkey = this.other.table[1];
        secondind = this.pullKey(state, "other", secondkey);
      }

      this.pullKey(state, "other", key);

      if (save && secondind !== undefined && secondind !== -1) {
        this.pushKey(state, "other", secondkey, secondind);
      }

      return sin;
    }
  };

  this.moveTableKey = function (state, skey, ttyp, tid) {
    var sid = this.keys.other.indexOf(skey);

    if (sid !== -1) {
      //var sin=
      this.delTableKey(state, skey);

      if (tid === undefined || tid === -1) {
        this.keys[ttyp] = this.keys[ttyp].concat(skey); // last position
      } else {
        state.Utils.spliceArray(this.keys[ttyp], tid, 0, skey);
      }

      return true;
    } else {
      return false;
    }
  };

  this.moveKey2Table = function (state, styp, skey, tkey) {
    //console.log("MoveKey2Table:",JSON.stringify(this.keys));
    var tid = this.keys.other.indexOf(tkey);
    var sid = this.keys[styp].indexOf(skey);

    if (tid !== -1 && sid !== -1 && skey !== tkey) {
      // exists and is not the same
      this.keys[styp][sid] = tkey;
      this.keys.other[tid] = skey;
      return true;
    } else {
      return false;
    }
  };

  this.moveTrash2Table = function (state, tkey) {
    var secondkey;
    var secondind;
    var tid = this.other.table.indexOf(tkey);

    if (tid !== -1) {
      var save = tid === 0;

      if (save) {
        secondkey = this.other.table[1];
        secondind = this.pullKey(state, "other", secondkey);
      }

      ;
      var tin = this.keys.other.indexOf(tkey);
      this.moveTrash(state, "other", tin + 1);

      if (save && secondind !== undefined && secondind !== -1) {
        this.pushKey(state, "other", secondkey, secondind);
      }

      return true;
    } else {
      return false;
    }
  };

  this.moveTrash = function (state, ttyp, tin) {
    state.Utils.spliceArray(this.keys[ttyp], tin, 0, this.trash);
    this.trash = [];
  };

  this.moveAllKey = function (state, styp, skey, ttyp, tid) {
    var lenp = this.keys[styp].length;
    var sid = this.keys[styp].indexOf(skey);

    if (sid !== -1) {
      var src = this.keys[styp].splice(sid, lenp - sid);
      state.Utils.spliceArray(this.keys[ttyp], tid, 0, src);
      return true;
    } else {
      return false;
    }
  };

  this.moveAllFirst = function (state, ttyp, tin) {
    var tlen = this.keys[ttyp].length;
    var src = this.keys[ttyp].splice(tin, tlen - tin);
    state.Utils.spliceArray(this.keys[ttyp], 0, 0, src);
  };

  this.filterKeys = function (state, arr) {
    var out = [];
    var alen = arr.length;

    for (var ii = 0; ii < alen; ii++) {
      var val = arr[ii];

      if (this.keyValOk(state, val)) {
        out.push(val);
      }
    } //console.log("CheckOk ",JSON.stringify(arr),"->",JSON.stringify(out));


    return out;
  };

  this.pullArrayKey = function (state, array, key) {
    var sin = array.indexOf(key);

    if (sin !== -1) {
      //var src=
      array.splice(sin, 1); // remove from path
    }

    ;
    return sin;
  }; //////////////////////////////// COL/ROW FUNCTIONS ///////////////////////


  this.getColKey = function (state) {
    var arr = state.Utils.cp(this.other.table);
    var pri = state.Layout.getPriorityIndex(state, arr);

    if (state.Layout.getLayoutMode(state) === state.Layout.code.layout.Map) {
      return "_lon";
    } else if (pri[this.other.table[0]] < pri[this.other.table[1]]) {
      return this.other.table[1];
    } else {
      return this.other.table[0];
    }
  };

  this.getRowKey = function (state) {
    var arr = state.Utils.cp(this.other.table);
    var pri = state.Layout.getPriorityIndex(state, arr);

    if (state.Layout.getLayoutMode(state) === state.Layout.code.layout.Map) {
      return "_lat";
    } else if (pri[this.other.table[0]] < pri[this.other.table[1]]) {
      return this.other.table[0];
    } else {
      return this.other.table[1];
    }
  };

  this.pushKey = function (state, typ, key, ind) {
    if (key !== undefined && key !== "") {
      state.Utils.spliceArray(this.keys[typ], ind, 0, key);
    }

    state.Utils.clean(this.keys[typ]);
  };

  this.pullKey = function (state, typ, key) {
    var sin = this.keys[typ].indexOf(key);

    if (sin !== -1) {
      var src = this.keys[typ].splice(sin, 1); // remove from path

      if (src !== key) {
        console.log("System error:", src, key);
      }

      return sin;
    }

    ;
  };

  this.pushColKey = function (state, key) {
    if (key !== undefined && key !== "") {
      state.Utils.spliceArray(this.keys.other, 0, 0, key);
    }
  };

  this.pushRowKey = function (state, key) {
    if (key !== undefined && key !== "") {
      state.Utils.spliceArray(this.keys.other, 1, 0, key);
    }
  };

  this.checkNewPath = function (state, styp, skey) {
    // check if values are defined for path
    var ok, ii;
    var sid = this.keys.path.indexOf(skey);

    if (styp === this.type.table.ptyp) {
      ok = true;
      var lent = this.other.table.length - 1;

      if (sid !== undefined) {
        lent = sid + 1;
      }

      ;

      for (ii = 0; ii <= lent; ii++) {
        var key = this.other.table[ii];

        if (this.keyVal(state, key) === undefined) {
          ok = false;
        }
