import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Chip from '@material-ui/core/Chip';

const styles = theme => ({
    key: {
	display: 'inline-block',
        marginRight: 'auto',
    },
    selectchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"red",
        borderColor:"blue",
    },
    tablechip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"green",
        borderColor:"blue",
    },
    restchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
    },
    othchip: {
        margin: theme.spacing.unit,
        color:"red",
        borderColor:"red",
  },
});
function getChipType(classes,keytype) {
    if (keytype === "select") {
	return classes.selectchip;
    } else if (keytype === "table") {
	return classes.tablechip
    } else if (keytype === "rest") {
	return classes.restchip
    } else if (keytype === "trash") {
	return classes.trashchip
    } else  {
	return classes.othchip
    };
};
class Key extends Component {
    render() {
        const { classes, state, keyitem, keytype } = this.props;
	var chip=getChipType(classes,keytype);
	var tpos=state.Path.trash.indexOf(keyitem);
	var onclick=() => state.Navigate.onClickPath(state,'trash',keyitem);
	var icon=null; //(tpos!==-1 ? <CloseIcon/> : null);
	return (
		<div className={classes.key}>
	 	   <Chip
	              icon={icon}
	              label={keyitem}
	              onClick={onclick}
	              className={chip}
	              variant="outlined"
		   />
		</div>
	);
    }
}

Key.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Key);
