import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import TrashIcon from '@material-ui/icons/Delete';

const styles = theme => ({
    config: {
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    var tpos=state.Path.trash.rest.indexOf(keyitem);
    var onclick=() => state.Navigate.onClickPath(state,'trash',keyitem);
    var chip=(tpos!==-1 ? classes.trashchip : classes.othchip);
    var icon=(tpos!==-1 ? <TrashIcon/> : null);
    return (<MenuItem key={keyitem}>
	     <Chip
	       icon={icon}
	       label={keyitem}
	       onClick={onclick}
	       className={chip}
	       variant="outlined"
	      />
	    </MenuItem>);
}
function Details(props) {
    const { classes, state, onclose, anchorEl } = props;
    var items=state.Path.trash.rest.concat(state.Path.other.rest);
    items=items.sort(state.Utils.ascending);
    var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
    return (<Menu
               id="simple-menu"
               anchorEl={anchorEl}
               open={Boolean(anchorEl)}
               onClose={onclose}
	      >
	      <MenuItem key={"close"} onClick={onclose}>Close</MenuItem>
	      {items.map(mapFunction)}
	   </Menu>);
}
class Config extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Config=this;
    };
    show(state,matrix) {
	this.setState();
    }
    state = {anchorEl: null,};
    handleClick = event => {this.setState({ anchorEl: event.currentTarget });};
    handleClose = () => {this.setState({ anchorEl: null });};
    handleX = () => {console.log("Action X")};
    render() {
        const { classes, state } = this.props;
	const { anchorEl } = this.state;
	return (
		<div className={classes.config}>
		 <Button
                    aria-owns={anchorEl ? 'simple-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.handleClick}
		   >
		   {<TrashIcon />}
                 </Button>
		<Details classes={classes} state={state} anchorEl={anchorEl} onclose={this.handleClose}/>
		</div>
	);
    }
}

Config.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Config);
