import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';

import ViewIcon from '@material-ui/icons/Visibility';
import NoViewIcon from '@material-ui/icons/VisibilityOff';
import KeyIcon from '@material-ui/icons/VpnKey';
import ChartIcon from '@material-ui/icons/BarChart';

import View    from './ViewComponent';
import Keys    from './KeysComponent';

const styles = theme => ({
    settings: {
        marginLeft: 'auto',
	color:'red',
    },
});
function ViewButton(props) {
    const {state}=props;
    var onclick=() => state.Show.toggleView(state);
    var mode=state.Show.viewMode;
    if (mode === state.Show.code.view.showPath) {
	return (
		<div className={classes.config}>
		 <Button
                    aria-owns={anchorView ? 'simple-menu' : undefined}
                    aria-haspopup="true"
                    onClick={onclick}
		   >
		   {<ViewIcon />}
                 </Button>
		</div>
	);
    } else {
	return (
		<div className={classes.config}>
		   <Button
                      aria-owns={anchorView ? 'simple-menu' : undefined}
                      aria-haspopup="true"
                      onClick={onclick}
		   >
		      {<NoViewIcon />}
                   </Button>
		</div>
	);
    }
}

class Settings extends Component {
    state = {anchorView:null,anchorKeys: null,};
    keysClick = event => {this.setState({ anchorKeys: event.currentTarget });};
    handleClose = () => {this.setState({ anchorView: null, anchorKeys:null });};
    render() {
        const { classes, state, anchor, onclose } = this.props;
	const { anchorView,anchorKeys } = this.state;
	return (
		<Menu
	          className={classes.config}
                  id="simple-menu"
                  anchorEl={anchor}
                  open={Boolean(anchor)}
                  onClose={onclose}
		>
		<ViewButton state={state}/>
		<KeyButton state={state}/>

		<div className={classes.config}>
		 <Button
                    aria-owns={anchorKeys ? 'simple-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.keysClick}
		   >
		   {<KeyIcon />}
                 </Button>
		</div>
		<Keys classes={classes} state={state} anchorEl={anchorKeys} onclose={this.handleClose}/>
		</Menu>
	);
    }
}

Settings.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Settings);
