import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { StickyTable, Row, Cell } from 'react-sticky-table';
import 'react-sticky-table/dist/react-sticky-table.css';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

//import TableCell from '@material-ui/core/TableCell';
//import TableSortLabel from '@material-ui/core/TableSortLabel';

console.log("Inside Table.")

const styles = theme => ({
    root: {
        flexGrow: 1,
	height: '100%',
	width: '100%',
    },
    paper: {
        padding: theme.spacing.unit * 2,
        textAlign: 'left',
    },
    table: {
        textAlign: 'left',
        padding: theme.spacing.unit * 2,
	cursor: "pointer",
	height: '100%',
	width: '100%',
    },
    cell : {cursor: "pointer",},
});

//function onCellClick(event) {
//    var rowid=event.target.getAttribute("rowid");
//    event.target.innerHTML = rowid;
//}
function getStep(state,colvalues) {
    var cellMode=state.Show.getLayoutMode(state)
    var step=1;
    if (cellMode === state.Show.code.mSeries) {step=Math.max(1,Math.ceil(colvalues.length/20));}
    return step;
}
// ---------------- DATA
function renderDataRow(classes,state,colkey,rowkey,colvalues,step,item,index) {
    var onclick="Navigate.onClickPath(state,'path','"+index+"');";
    var title=state.Database.getWhereDetail(colkey,colvalues[index]);
    if ((item-1)%step === 0) {
	return (<Cell classes={classes} state={state} key={`col-${colkey}`} index={index} onclick={onclick} title={title}/>);
    } else {
	return;
    }
};
function renderZeroRow(classes,state,colkey,colvalues,step,item,index) {
    if ((item-1)%step === 0) {
	return (<Cell classes={classes} state={state} key={`col-${colkey}`} index={index}/>);
    } else {
	return;
    }
};
function DataRows(props) {
    const { classes, state, step, colkey,colvalues, rowkey, rowvalues } = props;
    var mapFunction= (item,index)=>renderDataRow(classes,state,colkey,rowkey,colvalues,step,item,index);
    if (rowvalues.length===0) {
	return renderZeroRow(classes,state,colkey,colvalues,step,item,index);
    } else {
	return (rowvalues.map(mapFunction));
    }
}
// ---------------- HDR
function renderHdrCell(classes,state,colkey,colvalues,rowkey,step,item,index) {
    var title=state.Database.getWhereDetail(colkey,colvalues[index]);
    if ((item-1)%step === 0) {
        var colwhere = "";
        var cnt=0
	var clen=colvalues.length;
        for (var kk=item;kk<Math.min(clen,item+step);kk++) {
            if (colwhere != "") {colwhere=colwhere + " or ";}
            colwhere=colwhere + state.Database.getWhereDetail(colkey,colvalues[kk]);
            cnt=cnt+1;
        };               
	var onclick=() => state.Layout.selectKey(colkey,colvalues[index],colwhere,cnt);
	var title=colwhere;
	return (<Cell classes={classes} state={state} key={`col-${colkey}`} index={index} onclick={onclick} title={title}/>);
    } else {
	return;
    }
}
function FirstHdrCell (props) {
    const { colkey, rowkey } = props;
    return (<Cell>rowkey + "\\" + colkey </Cell>);
}
function HdrRow(props) {
    const { classes, state, step, colkey, colvalues, rowkey } = props; //, rowvalues
    var mapFunction= (item,index)=>renderHdrCell(classes,state,colkey,colvalues,rowkey,step,item,index);
    return (<Row><FirstHdrCell colkey={colkey} rowkey={rowkey}/>{colvalues.map(mapFunction)}</Row>);
}
// ---------------- Details
function Details(props) {
    const { state } = props; // classes, 
    var colkey=state.Path.getColKey(state)||"";
    var rowkey=state.Path.getRowKey(state)||"";
    var colvalues=state.Path.filterKeys(state,state.Matrix.values[colkey]||[""]);
    var rowvalues=state.Path.filterKeys(state,state.Matrix.values[rowkey]||[""]);
    var step=getStep(state,colvalues);
    return (<div  style={{width: '100%', height: '100%'}}>
        <StickyTable state={state}>
	      <HdrRow   state={state} step={step} colkey={colkey} colvalues={colvalues} rowkey={rowkey} rowvalues={rowvalues}/>
	      <DataRows state={state} step={step} colkey={colkey} colvalues={colvalues} rowkey={rowkey} rowvalues={rowvalues}/>
        </StickyTable>
           </div>);
 }
class Table extends Component {
    constructor(props) {
	super(props);
	this.state=props.state;
    }
    componentDidMount() {} 
    render() {
	const { classes, state } = this.props;
	console.log("##### Rendering Table2D.");
	return (<div className={classes.root}  style={{width: '100%', height: '100%'}}>
		   <Grid container spacing={24}>
		      <Grid item xs={12} > 
                         { <Paper className={classes.table}>
		              <Details state={state} classes={classes}/>
                           </Paper>}
                      </Grid>
                   </Grid>
	        </div>);
    }
}

Table.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Table);
