import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

import SummaryCell from './SummaryCell';
import SeriesCell  from './SeriesCell';
import CanvasText  from './CanvasTextComponent';
import Text        from './TextComponent';

//	overflow: 'hidden',

const styles = theme => ({
    root: {
	height: '100%',
    },
    paper: {
	tableLayout: 'fixed',
	padding:0,
	margin:0,
    },
    divTable :{
	display: 'table',
	width: '100%',
    },
    divTableRow:  {
	display: 'table-row',
    },
    divTableCell:{
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '3px 3px',
    },
    divTableCellCursor:{
	cursor: "pointer",
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableHead : {
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableHeading : {
	display: 'table-header-group',
    },
    divTableHeadingCenter : {
	display: 'flex',
	justifyContent: 'center',
    },
    divTableFoot : {
	backgroundColor: '#DDD',
	display: 'table-footer-group',
	fontWeight: 'bold',
    },
    divTableBody : {
	display: 'table-row-group',
    }
});
// ---------------- DATA
function DataCell(props) {
    const {classes,state,elements,mode,plan,...other}=props;
    if (elements===undefined) {
	return <div className={classes.divTableCell} style={{backgroundColor:'#EEE'}}/>
    } else if (mode===state.Show.code.cell.Sum) {
	return <SummaryCell {...other} state={state} elements={elements} plan={plan}/>
	//return null;
    } else {
	return <SeriesCell {...other} state={state} elements={elements} plan={plan}/>
	//return null;
    }
};
function renderDataSeries(classes,state,range,plans) {
    var elements=state.Matrix.getMatrixRowElements("","",state.React.matrix,plans.cell.step);
    console.log("Making data cell:",JSON.stringify(plans.cell),JSON.stringify(elements));
    return (<DataCell classes={classes} state={state} elements={elements} plan={plans.cell} range={range} key={"key"}/>);
};
function renderDataSum(classes,state,doc,plan,skey,bgcolor,index){
    if (doc===undefined) {
	return <div className={classes.divTableCell} style={{backgroundColor:'#EEE'}}/>
    } else {    
	var val=doc[skey];
	if (val === undefined) {val="";};
	//console.log("Key:",skey," Val:",JSON.stringify(val),JSON.stringify(doc[skey]));
	return (
            <div className={classes.divTableCell} style={{backgroundColor:bgcolor}} key={skey}>
		{val}
	    </div>
	);
    }
};
function renderDoc(classes,state,skeys,plan,doc,lev,index) {
    var rowwhere="";
    //console.log("We have a matrix(",rowval,") with range:",JSON.stringify(range));
    var bgcolor=state.Colors.getLevelColor(lev);
    var mapFunction= (skey,index)=>renderDataSum(classes,state,doc,plan,skey,bgcolor,index);
    return (<div className={classes.divTableRow} key={index.toString()}>
	    {skeys.map(mapFunction)}
	    </div>);
};
function dataRowSum(classes,state,skeys,plans) {
    var rowwhere="";
    //console.log("We have a matrix with range:",JSON.stringify(range));
    var element=state.Matrix.getMatrixElement("","",state.React.matrix);
    if (element === undefined) {
	return (<div className={classes.divTableRow} key={"row"}>
		</div>);
    } else if (element.docs.length < 100) {
        var lev=element.maxlev;
	var mapFunction= (doc,index)=>renderDoc(classes,state,skeys,plans.cell,doc,lev,index);
	return element.docs.map(mapFunction);
    } else {
	return (<div className={classes.divTableRow} key={"row"}>
		{"..."}
		</div>);
    };
};
function dataRowSeries(classes,state,plans) {
    //console.log("We have a matrix with range:",JSON.stringify(range));
    var range=[undefined,undefined];
    if (state.React.matrix!==undefined) {
	range=state.Matrix.getRange(state,state.React.matrix,[""],[""]);
	//console.log("Range:",JSON.stringify(range));
    };
    return (<div className={classes.divTableRow} key={"row"}>
	    {renderDataSeries(classes,state,range,plans)}
	    </div>);
};
function renderZeroRow(classes,state,plans) {
    return (<div className={classes.divTableRow} key={1}>
	       <div className={classes.divTableCell} width={plans.cell.width}>No data available</div>
	    </div>);
};
function DataRows(props) {
    const { classes, state, plans, cellMode, skeys } = props;
    if (cellMode === state.Show.code.cell.Sum) {
	return dataRowSum(classes,state,skeys,plans);
    } else {
	return dataRowSeries(classes,state,plans);
    }
}
// ---------------- HDR
function renderHdrSum(classes,state,plan,val,index) {
    //console.log("HdrCell:",val);
    var cursor=classes.divTableCell;
    return (<div className={cursor} style={{backgroundColor:'#DDD'}} key={`col-${index}`} >
	    {val}
 	    </div> );
}
function HdrRow(props) {
    const { classes, state, plans, label, skeys, cellMode } = props; //, rowvalues
    if (cellMode === state.Show.code.cell.Sum) {
	var mapFunction= (val,index)=>renderHdrSum(classes,state,plans.col,val,index);
	//console.log("Making header Sum row.",label,JSON.stringify(skeys));
	return (<div className={classes.divTableRow} key={'hdr'}>
		   {skeys.map(mapFunction)}
		</div>);
    } else {
	return null;
    }
}
// ---------------- Details
function Details(props) {
    const { classes, state } = props; // classes, 
    var cellMode  = state.Show.getCellMode(state);
    if (state.React.matrix === undefined) {
	return (<div className={classes.divTable}>
		   <div className={classes.divTableBody}>
		      <div className={classes.devTableCell}>
		         {"No Matrix defined"}
		      </div>
		   </div>
		</div>
	       );
    } else {
        var skeys = state.Matrix.sortedKeys(state,state.Matrix.keyCnt);
 	//DOM.style.font
	var border=2;
	var label="";
	var width=0.8*window.innerWidth;
	var height=0.8*(window.innerHeight-200);
	var plans=state.Layout.makePlans(label,[""],[""],width,height,border,cellMode);
	//console.log("Details => Width/Height:",window.innerWidth,window.innerHeight,plan.cell.width,plan.hdr.height)
	return (<div className={classes.divTable}>
		   <div className={classes.divTableBody}>
		      <HdrRow classes={classes} state={state} key={"hdr"} plans={plans} label={label} cellMode={cellMode} skeys={skeys}/>
		      <DataRows classes={classes} state={state} key={"data"} plans={plans} cellMode={cellMode} skeys={skeys}/>
		   </div>
		</div>
	       );
    }
};

class Table0D extends Component {
    constructor(props) {
	super(props);
	//this.state=props.state;
    }
    componentDidMount() {
        window.addEventListener("resize", this.updateWindowDimensions);
    } 
    updateWindowDimensions = () => {
        this.width= window.innerWidth;
	this.height=window.innerHeight;
	this.bbx=this.el.getBoundingClientRect();
	//console.log("Width/Height:",this.width,this.height,this.bbx.width,this.bbx.height)
    };
    element(el) {
	if (el !== undefined && el !== null) {
	    this.el=el;
	    this.bbx=this.el.getBoundingClientRect();
	    //console.log("BBX width/height:",this.bbx.width,this.bbx.height);
	};
    };
    render() {
	const { classes, state } = this.props;
	//console.log("##### Rendering Table0D.");
	return (<div ref={el=>{this.element(el)}} className={classes.root}  style={{width: '100%', height: '100%'}}>
		 <Grid container spacing={24}>
		  <Grid item xs={12} > 
                   { <Paper className={classes.paper}>
		       <Details state={state} classes={classes} element={this}/>
                     </Paper>}
                  </Grid>
                 </Grid>
	        </div>);
	}
}

Table0D.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Table0D);
