import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import SelectIcon from '@material-ui/icons/Star';
import Values from './ValuesComponent';

const styles = theme => ({
    settings:{},
    selects: {
	display: 'inline-block',
        marginRight: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        bselectColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        bselectColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    return (<MenuItem key={keyitem} className={classes.selects}>
	    <Values classes={classes} state={state} keyitem={keyitem}/> 
	    </MenuItem>);
}
class Selects extends Component {
    state={anchor:null};
    handleClick = event => {this.setState({ anchor: event.currentTarget });};
    handleClose = () => {this.setState({ anchor: null });};
    render() {
        const { classes, state } = this.props;
	var items=state.Path.keys.path;
	//items=items.sort(state.Utils.ascending);
	var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
	//console.log("Selects.rendering",items.length,JSON.stringify(anchor),Boolean(anchor));
	return (
		<div className={classes.selects}>
		   <Button
                      aria-owns={this.state.anchor ? 'selects-menu' : undefined}
                      aria-haspopup="true"
                      onClick={this.handleClick}
		    >
	  	       {<SelectIcon state={state}/>}
                     </Button>
		     <Menu
	                className={classes.selects}
                        id="selects-menu"
	                anchorEl={this.state.anchor}
                        open={Boolean(this.state.anchor)}
                        onClose={this.handleClose}
		     >
		        {items.map(mapFunction)}
	             </Menu>
		</div>
	);
    }
}

Selects.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Selects);
