import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    canvas: {
	width:"100%",
	height:"100%",
	overflow:"hidden",
    },
    pointer: {
	cursor:"pointer"
    },
    nopointer: {
    },
    div :{},
});
function drawThresholds(state,elements,colkey,colvalues,index,range,plan,level,ctx,height,dw,tot) {
    //console.log("Canvas:",cnv.width,cnv.height);
    const step=plan.step;
    var clen=0; if (colvalues !== undefined) {clen=colvalues.length;}; // number of columns
    var elen=elements.length;
    var blev=-1;
    var mlev=0;
    var ee;
    var cnt=0;
    for (ee=0;ee<elen;ee++) {   // loop over elements
	var el=elements[ee];
	var docs=el.docs;
	var dlen=docs.length;
	//console.log("   El:",colkey,ee,el.maxlev,blev); // JSON.stringify(lev),JSON.stringify(d),
	console.log("Element:",el.maxlev,tot,cnt,dlen,JSON.stringify(range));
	if (dlen>0 && range !== undefined) {
	    var color=state.Colors.getLevelColor(el.maxlev);
	    blev=Math.max(blev,el.maxlev);
	    mlev=Math.min(mlev,el.minlev);
	    for (var jj=0;jj<dlen;jj++) {   // loop over segments in each element
		cnt=cnt+1;
		var d=docs[jj];
		console.log("   Doc:",colkey,jj,d.level,col,JSON.stringify(lev),JSON.stringify(d)); // 
		for (var ii=index;ii<Math.min(index+step,clen);ii++) {
		    console.log("   Checking Val:",colkey,ii,colvalues[ii],d[colkey]);
		    //console.log("Position:",ii,jj,dlen,step,colkey,rowkey,colvalues[ii],d[colkey])
		    if (colvalues[ii]==="" || d[colkey]  === colvalues[ii]) {
			var thr=d._thr;
			//console.log("Making canvas:",ii,colvalues[ii],color,JSON.stringify(d),
			//	    " Thr=",JSON.stringify(t),width,height,JSON.stringify(range));
			console.log("CanvasGraph:",ii,jj,d.dtg,color,el.maxlev);
			var min=range[0]
			var max=range[1];
			var mm=ii-index
			if (step<tot) {mm=cnt-1;}
			//console.log(" canvas position:",mm,ii,cnt,step,tot)
			var xmin=mm*dw;       // width/10;
			var xmax=(mm+1)*dw;   //width-xmin;
			if (color !== undefined) {
			    var vals;
			    if (thr.min !== undefined && thr.val !== undefined) {
				vals=thr.min;
			    }
			    if (thr.max !== undefined && thr.val !== undefined) {
				vals=thr.max;
			    }
			    var vlen=vals.length;
			    for (var ll=0;ll<vlen;ll++) {
				var tyval=vals[ll];
				var tzval=state.Show.scale(tyval,min,max,height,0);
				var scolor=undefined;
				if (ll === level) {
				    scolor='black';
				} else {
				    scolor=state.Colors.getLevelColor(ll);
				};
				ctx.beginPath();
				ctx.lineWidth=1;
				if (scolor !== undefined) {ctx.strokeStyle=scolor;}
				ctx.moveTo(xmin,tzval);
				ctx.lineTo(xmax,tzval);
				ctx.stroke();
				//console.log("Stroke color:",scolor,ll,tzval,cnv.width);
			    }
			}
		    }
		}
	    }
	}
    };
    return blev;
};
function drawData(state,elements,colkey,colvalues,index,range,plan,level,ctx,height,dw,tot) {
    //console.log("Canvas:",cnv.width,cnv.height);
    const step=plan.step;
    var clen=0; if (colvalues !== undefined) {clen=colvalues.length;}; // number of columns
    var elen=elements.length;
    var blev=-1;
    var mlev=0;
    var ee;
    var cnt=0;
    for (ee=0;ee<elen;ee++) {   // loop over elements
	var el=elements[ee];
	var docs=el.docs;
	var dlen=docs.length;
	//console.log("   El:",colkey,ee,el.maxlev,blev); // JSON.stringify(lev),JSON.stringify(d),
	//console.log("Element:",el.maxlev,color,tot,cnt,dlen);
	if (dlen>0 && range !== undefined) {
	    var color=state.Colors.getLevelColor(el.maxlev);
	    blev=Math.max(blev,el.maxlev);
	    mlev=Math.min(mlev,el.minlev);
	    for (var jj=0;jj<dlen;jj++) {   // loop over segments in each element
		cnt=cnt+1;
		var d=docs[jj];
		var lev=state.Threshold.getLevel(state,d);
		var col=state.Colors.getLevelColor(lev);
		//console.log("   Doc:",colkey,jj,d.level,col,JSON.stringify(lev),JSON.stringify(d)); // 
		//console.log("   Doc:",JSON.stringify(colkey),JSON.stringify(colvalues),clen,index); // 
		for (var ii=index;ii<Math.min(index+step,clen);ii++) {
		    //console.log("   Checking Val:",colkey,rowkey,ii,colvalues[ii],d[colkey]);
		    //console.log("Position:",ii,jj,dlen,step,colkey,rowkey,colvalues[ii],d[colkey])
		    if (colvalues[ii]==="" || d[colkey]  === colvalues[ii]) {
			var thr=d._thr;
			var vals;
			//console.log("Making canvas:",ii,colvalues[ii],color,JSON.stringify(d),
			//	    " Thr=",JSON.stringify(t),width,height,JSON.stringify(range));
			//console.log("CanvasGraph:",ii,jj,d.dtg,color,el.maxlev,JSON.stringify(t));
			var min=range[0]
			var max=range[1];
			var ymin=min;
			if (thr.min !== undefined && thr.val !== undefined) {
			    ymin=thr.val
			    vals=thr.min;
			}
			var ymax=max;
			if (thr.max !== undefined && thr.val !== undefined) {
			    ymax=thr.val;
			    vals=thr.max;
			}
			var mm=ii-index
			if (step<tot) {mm=cnt-1;}
			//console.log(" canvas position:",mm,ii,cnt,step,tot)
			var xmin=mm*dw;       // width/10;
			var xmax=(mm+1)*dw;   // width-2*xmin;
			var zmin=state.Show.scale(ymin,min,max,height,0);
			var zmax=state.Show.scale(ymax,min,max,height,0);
			//console.log("Fill:",xmin,xmax,zmin,zmax,ymin,ymax,min,max,height);
			//console.log("Fill:",xmin,xmax,zmin,(zmax-zmin),height,color);
			//ctx.fillStyle="cornflowerblue";
			color=col;
			if (color !== undefined) {
			    var dx=Math.max(1,xmax-xmin);
			    var dz=zmax-zmin;
			    //if (Math.abs(dz)/height < 0.5) {
			    //ctx.fillStyle="black";
			    //ctx.fillRect(xmin,zmin,dx,dz);
			    //} else {
			    ctx.strokeStyle='black';
			    ctx.strokeRect(xmin,zmin,dx,dz);
			    //}
			    //
			    //console.log("rect:",lev,xmin,xmax,zmin,zmax);
			    //
			};
		    }
		}
	    }
	}
    };
    if (mlev < 0) {
	return mlev;
    } else {
	return blev;
    }
};
function drawMarker(state,plan,level,ctx,height,dw,tot) {
    ctx.strokeStyle='blue';
    ctx.beginPath();
    ctx.moveTo(0,0);
    ctx.lineTo(dw*tot,height);
    ctx.moveTo(dw*tot,0);
    ctx.lineTo(0,height);
    ctx.strokeRect(0,0,dw*tot,height);
    ctx.stroke();
};
function updateCanvas(item) {
    const {state,elements,colkey,colvalues,index,range,plan,level} = item.props;
    const cnv=item.refs.canvas;        // canvas
    const ctx = cnv.getContext('2d');  // context
    //console.log("Canvas:",cnv.width,cnv.height);
    const step=plan.step;
    var height = cnv.height;
    var elen=elements.length;
    var tot=0; for (var ee=0;ee<elen;ee++) {tot=tot+elements[ee].docs.length;}; // total number of segments
    var dw= cnv.width/Math.max(step,tot); // width of graph segment
    ctx.clearRect(0, 0, cnv.width, cnv.height);
    drawThresholds(state,elements,colkey,colvalues,index,range,plan,level,ctx,height,dw,tot);
    var lev=drawData(state,elements,colkey,colvalues,index,range,plan,level,ctx,height,dw,tot);
    if (lev>=0) {
	// data is ok
    } else { // invalid data
	drawMarker(state,plan,level,ctx,height,dw,tot);
    }
};
class CanvasGraphComponent extends React.Component {
    componentDidMount() {
        updateCanvas(this);
    }
    componentDidUpdate() {
        updateCanvas(this);
    }
    render() {
        const { classes, onclick, title, plan, ...other } = this.props;
        return (
		<canvas {...other} className={classes.canvas} classes={classes} onClick={onclick} title={title} 
	            plan={plan} width={plan.width} height={plan.height} ref="canvas"/>
        );
    }
}

// width={plan.width} height={plan.height}

CanvasGraphComponent.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(CanvasGraphComponent);
