import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableHead from '@material-ui/core/TableHead';
import TableCell from '@material-ui/core/TableCell';
import TableSortLabel from '@material-ui/core/TableSortLabel';

import SummaryCell from './SummaryCell';
import SeriesCell from './SeriesCell';

console.log("Inside Table.")

const styles = theme => ({
    root: {
        flexGrow: 1,
	height: '100%',
	width: '100%',
    },
    paper: {
        padding: theme.spacing.unit * 2,
        textAlign: 'left',
    },
    table: {
        textAlign: 'left',
        padding: theme.spacing.unit * 1,
	height: '100%',
	width: '100%',
	borderStyle: 'solid',
    },
    row  :{alignItems: "stretch"},
    cell : {cursor: "pointer",
	    border: "1px"},
});

//function onCellClick(event) {
//    var rowid=event.target.getAttribute("rowid");
//    event.target.innerHTML = rowid;
//}
function getStep(state,colvalues,cellMode) {
    var step=1;
    if (cellMode === state.Show.code.mSeries) {step=Math.max(1,Math.ceil(colvalues.length/20));}
    return step;
}
// ---------------- DATA
function FirstDataCell (props) {
    const { classes, rowval, onclick, title } = props;
    console.log("Making first data cell.");
    return (<TableCell className={classes.cell} onClick={onclick} title={title}>{rowval}</TableCell>);
}
function DataCell(props) {
    const {state,mode,...other}=props;
    if (mode===state.Show.code.mSum) {
	return <SummaryCell {...other} state={state}/>
    } else {
	return <SeriesCell {...other} state={state}/>
    }
}
function renderDataCell(classes,state,colkey,colvalues,rowkey,rowval,rowindex,rowwhere,mode,step,val,index) {
    var title=state.Database.getWhereDetail(colkey,colvalues[index]);
    console.log("Making data cell:",rowval,val,index,step);
    if ((index-1)%step === 0) {
	var clen=colvalues.length;
	// get elements and range
	var kk;
        var elements=undefined
	var range=[undefined,undefined];
	if (state.React.matrix!==undefined) {
	    range=state.Matrix.getRange(state.React.matrix,colvalues,[rowval]);
            for (kk=index;kk<Math.min(clen,index+step);kk++) {
		var element=state.Matrix.getMatrixElement(colvalues[kk],rowval,state.React.matrix);
		if (element === undefined) {
		} else {
                    //console.log("Found:",colvalues[kk],rowval);
                    if (elements===undefined) {elements=[];};
                    elements.push(element);
		}
            };               
	}
	// get count and colwhere
        var cnt=0;
        var colwhere = "";
        for (kk=index;kk<Math.min(clen,index+step);kk++) {
	    if (colwhere !== "") {colwhere=colwhere + " or ";}
	    colwhere=colwhere + state.Database.getWhereDetail(colkey,colvalues[kk]);
	    cnt=cnt+1;
        };               
	// make onclick and title
	var onclick=() => state.Layout.selectItem(colkey,rowkey,colvalues[index],rowval,colwhere,rowwhere,cnt,1);
	return (<DataCell classes={classes} state={state} key={`col-${index}`} index={index} onClick={onclick} title={title} 
		colkey={colkey} rowkey={rowkey} colval={val} rowval={rowval} colwhere={colwhere} rowwhere={rowwhere} 
		elements={elements} mode={mode} step={step} range={range}/>);
    } else {
	return null;
    }
}
function dataRow(classes,state,colkey,rowkey,colvalues,mode,step,rowval,rowindex) {
    var rowwhere=state.Database.getWhereDetail(rowkey,rowval);
    var onclick=() => state.Layout.selectKey(rowkey,rowval,rowwhere,1);
    var mapFunction= (val,index)=>renderDataCell(classes,state,colkey,colvalues,rowkey,rowval,rowindex,rowwhere,mode,step,val,index);
    return (<TableRow key={rowindex.toString()}>
	    <FirstDataCell classes={classes} key={'0'} rowval={rowval} onClick={onclick} title={rowwhere}/>
	    {colvalues.map(mapFunction)}
	    </TableRow>);
};
function renderZeroRow(classes,state,colkey,colvalues,step) {
    return (<TableRow key={'1'}>
	    <TableCell  key={`0`} classes={classes} state={state} index={'0'}>No data</TableCell>
	    </TableRow>);
};
function DataRows(props) {
    const { classes, state, step, colkey,colvalues, rowkey, rowvalues, mode } = props;
    console.log("Making data rows.",rowkey,JSON.stringify(rowvalues));
    var mapFunction= (val,index)=>dataRow(classes,state,colkey,rowkey,colvalues,mode,step,val,index);
    if (rowvalues.length===0) {
	return renderZeroRow(classes,state,colkey,colvalues,step);
    } else {
	return (rowvalues.map(mapFunction));
    }
}
// ---------------- HDR
function FirstHdrCell (props) {
    const { colkey, rowkey } = props;
    var label=rowkey + "\\" + colkey;
    console.log("Making first header cell.",label);
    return (<TableCell>{label}</TableCell>);
}
function renderHdrCell(classes,state,colkey,colvalues,rowkey,step,val,index) {
    if ((index-1)%step === 0) {
	//console.log("HdrCell:",index,step);
        var colwhere = "";
        var cnt=0
	var clen=colvalues.length;
        for (var kk=index;kk<Math.min(clen,index+step);kk++) {
            if (colwhere !== "") {colwhere=colwhere + " or ";}
            colwhere=colwhere + state.Database.getWhereDetail(colkey,colvalues[kk]);
            cnt=cnt+1;
        };               
	var onclick=() => state.Layout.selectKey(colkey,colvalues[index],colwhere,cnt);
	var title=colwhere;
	return (<TableCell className={classes.cell} key={`col-${index}`} index={index} onClick={onclick} title={title}>{val}</TableCell>);
    } else {
	return null;
    }
}
function HdrRow(props) {
    const { classes, state, step, colkey, colvalues, rowkey } = props; //, rowvalues
    console.log("Making header row.",colkey,JSON.stringify(colvalues));
    var mapFunction= (val,index)=>renderHdrCell(classes,state,colkey,colvalues,rowkey,step,val,index);
    return (<TableRow key={0}>
	      <FirstHdrCell colkey={colkey} rowkey={rowkey}/>
	      {colvalues.map(mapFunction)}
	     </TableRow>);
}
// ---------------- Details
function Details(props) {
    const { classes, state } = props; // classes, 
    var colkey = state.Path.getColKey(state)||"";
    var rowkey = state.Path.getRowKey(state)||"";
    var colvalues = state.Path.filterKeys(state,state.Matrix.values[colkey]||[""]);
    var rowvalues = state.Path.filterKeys(state,state.Matrix.values[rowkey]||[""]);
    var cellMode  = state.Show.getCellMode();
    var step=getStep(state,colvalues,cellMode);
    return (<div  style={{width: '100%', height: '100%'}}>
        <Table>
	    <TableHead>
	      <HdrRow classes={classes} state={state} step={step} colkey={colkey} colvalues={colvalues} rowkey={rowkey} rowvalues={rowvalues}/>
	    </TableHead>
	    <TableBody>
	      <DataRows classes={classes} state={state} step={step} colkey={colkey} colvalues={colvalues} rowkey={rowkey} rowvalues={rowvalues} mode={cellMode}/>
	    </TableBody>
        </Table>
           </div>);
 }

function onCellClick(event) {
    var rowid=event.target.getAttribute("rowid");
    event.target.innerHTML = rowid;
}
class Table2D extends Component {
    constructor(props) {
	super(props);
	this.state=props.state;
    }
    componentDidMount() {} 
    render() {
	const { classes, state } = this.props;
	console.log("##### Rendering Table2D.");
	return (<div className={classes.root}  style={{width: '100%', height: '100%'}}>
		   <Grid container spacing={24}>
		      <Grid item xs={12} > 
                         { <Paper className={classes.table}>
		              <Details state={state} classes={classes}/>
                           </Paper>}
                      </Grid>
                   </Grid>
	        </div>);
    }
}

Table2D.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Table2D);
