import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';

import ViewIcon from '@material-ui/icons/Eye';
import KeyIcon from '@material-ui/icons/Key';

import View    from './ViewComponent';
import Keys    from './KeysComponent';

const styles = theme => ({
    settings: {
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    var tpos=state.Path.trash.rest.indexOf(keyitem);
    var onclick=() => state.Navigate.onClickPath(state,'trash',keyitem);
    var chip=(tpos!==-1 ? classes.trashchip : classes.othchip);
    var icon=null; //(tpos!==-1 ? <CloseIcon/> : null);
    return (<MenuItem key={keyitem}>
	    <Button
               aria-owns={anchorSettings ? 'simple-menu' : undefined}
               aria-haspopup="true"
               onClick={this.handleClick}
	    >
	       {<SettingsIcon />}
            </Button>
	    <Chip
	       icon={icon}
	       label={keyitem}
	       onClick={onclick}
	       className={chip}
	       variant="outlined"
	    />
	    </MenuItem>);
}
function Details(props) {
    const { classes, state, onclose, anchor } = props;
    var items=state.Path.trash.rest.concat(state.Path.other.rest);
    items=items.sort(state.Utils.ascending);
    var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
    return (<Menu
               id="simple-menu"
               anchorEl={anchor}
               open={Boolean(anchor)}
               onClose={onclose}
	      >
	      {items.map(mapFunction)}
	   </Menu>);
}
class Settings extends Component {
    state = {anchorView:null,anchorKeys: null,};
    viewClick = event => {this.setState({ anchorView: event.currentTarget });};
    keysClick = event => {this.setState({ anchorKeys: event.currentTarget });};
    handleClose = () => {this.setState({ anchorView: null, anchorKeys:null });};
    handleX = () => {console.log("Action X")};
    render() {
        const { classes, state, anchor } = this.props;
	const { anchorView,anchorKeys } = this.state;
	return (
		<div className={classes.settings}>
		 <Button
                    aria-owns={anchorView ? 'simple-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.viewClick}
		   >
		   {<ViewIcon />}
                 </Button>
		 <Button
                    aria-owns={anchorKeys ? 'simple-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.keysClick}
		   >
		   {<ViewIcon />}
                 </Button>
		<Keys classes={classes} state={state} anchorEl={anchorKeys} onclose={this.handleClose}/>
		</div>
	);
    }
}

Settings.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Settings);
