//console.log("Loading LayoutLib.js");

function Layout() {
    this.rotate=undefined;  // should labels on x-axis be rotated?
    this.priority=undefined;// which key should be on the abscissa
    this.order={};          // key order
    this.trash={};           // key trash
    this.init=function(state){
	var par="Table"+state.Utils.version
	state.Utils.init(par,this);
    };
    // adjust keys so that rows/cols have more than on entry...
    this.getPriorityKeys=function(state){
        if (state.Layout.priority === undefined) {
            state.Layout.priority=state.Database.keyCnt.keys();
        } else {
	    var keys = Object.keys(state.Database.keyCnt);;
	    var plen = keys;
	    for (var ii = 0; ii < plen; ii++) {
		var key=keys[ii];
		if (state.Layout.priority.indexOf(key)===-1) {
		    state.Layout.priority.push(key);
		}
	    };
	};
	return state.Layout.priority; //state.Utils.invertedArray()
    };
    this.increasePriority=function(state,key){
	var kid=state.Layout.priority.indexOf(key);
	console.log("Bumping:",key,kid,JSON.stringify(state.Layout.priority));
	if (kid !== -1 && kid > 0) {
	    var src=state.Layout.priority.splice(kid, 1); // remove from array   
	    state.Utils.spliceArray(state.Layout.priority,kid-1,0,src);
	}
	state.Show.showConfig(state);
    };
    this.checkTableKeys=function(state) {
	var ret=[];
	var where = state.Database.getWhere(state);
	state.Matrix.initKeyCnt(state);
	state.Matrix.mapKeyCnt(state,where,0,state.Path.keys.other); // possible table keys (no undefined)
	state.Path.exportAllKeys(state);
	//console.log("this.checkTableKeys Entering.",JSON.stringify(setup));
	var keys=state.Path.other.table;
	var bdone= ! keys.length < 2;
	var iinew=-1;
	var jjnew=-1;
	var order=state.Utils.cp(state.Path.keys.other);
	do {
	    //console.log("this.checkTableKeys Looping.");
	    if (keys.length < 2) {
		bdone=true;
	    } else {
		// check for number of column values
		var docs=state.Database.getCntDocs(state,where,state.Path.other.table); // current table keys
		var slen=state.Path.other.table.length;
		var hits={};
		var maxh={};
		var kval={};
		var ii,jj,key,val;
		// loop over docs
		var dlen = docs.length;
		for (ii = 0; ii < dlen; ii++) {
    		    var doc=docs[ii];
		    //console.log("Trash doc=",ii,JSON.stringify(doc));
		    for (jj=0;jj<slen;jj++) {
			key=state.Path.other.table[jj];
			if (doc[key] !== undefined) {
			    val=doc[key];
			    kval[key]=val;
			    if (hits[key]  === undefined) {hits[key]={};}
			    hits[key][val] = 1+ (hits[key][val]||0);
			    //console.log("Found:",key,val,hits[key][val]);
			    if (hits[key][val] > (maxh[key]||0)) {
				maxh[key]=hits[key][val];
			    }
			}
		    }
		}
		//console.log("this.checkTableKeys Checking.",dlen);
		var rm={};
		bdone=true;
		//console.log("Max:",JSON.stringify(maxh));
		for (jj=0;jj<slen;jj++) {
		    key=state.Path.other.table[jj];
		    rm[key]="";
		    //console.log("Checking:",key,JSON.stringify(maxh));
		    if (maxh[key] !== undefined) {
			if (maxh[key]  === 1) {
			    rm[key]="other";
			} else if (maxh[key]  === dlen) { // remove key from table
			    rm[key]="self";
			}
		    }
		}
		var rmthis={};
		for (jj=0;jj<slen;jj++) {
		    key=state.Path.other.table[jj];
		    if (rm[key]  === "other") {
			for (var kk=0;kk<slen;kk++) {
			    var rkey=state.Path.other.table[kk];
			    if (kk !== jj) {
				rmthis[rkey]="trash";
				iinew=kk;
			    }
			}
		    } else if (rm[key]  === "self") {
			rmthis[key]="path";
			jjnew=jj;
		    }
		}
		if (iinew !== -1 && jjnew !== -1) {
		    iinew=-1;
		    jjnew=-1;
		}
		//console.log("Rm:",JSON.stringify(rmthis),JSON.stringify(rm));
		for (jj=0;jj<slen;jj++) {
		    key=state.Path.other.table[jj];
		    if (rmthis[key] !== undefined) {
			bdone=false;
			var sin=state.Path.keys.other.indexOf(key);
			if (sin !== -1) {
			    if (jj  === 0) {
				// flipTable
				//console.log("Flipped table.",JSON.stringify(state.Path.keys),this.getFlipString(state));
			    } else {
				//console.log("Not Flipping table.",JSON.stringify(state.Path.keys),this.getFlipString(state));
			    };
			    var src=state.Path.keys.other.splice(sin, 1); // remove from path
			    if (rmthis[key]  === "trash") {// trash key
				//console.log("### Trashing:",key);
				state.Path.keys.trash=state.Path.keys.trash.concat(src); // last path position
				ret=ret.concat(src);
			    } else if (rmthis[key]  === "path") {// add to path
				// add to path...
				val=kval[key];
				//console.log("### Pathing:",key,val);
				state.Path.keys.path=state.Path.keys.path.concat(src); // last path position
				state.Path.select.val[key]=[val];
				state.Path.select.where[key]=key+'="'+val+'"'
				state.Path.select.cnt[key]=1;
				this.order[key]=order;
				//this.last.flip[key]=this.getFlip(state);
			    };
			}
		    }
		}
		if (! bdone) {state.Path.exportAllKeys(state);}
	    };
	} while (! bdone);
	//console.log("this.checkTableKeys Done.",JSON.stringify(state.Path.other.table));
	return ret;
    };
    this.getDescription=function(state,element,skeys) {
	if (element.cnt  === 1) {
	    var s="";
	    var docs=element.docs;
	    var doc=docs[0];
	    var klen=skeys.length;
	    for (var jj = 0; jj < klen; jj++) {
		var d=skeys[jj]+"="+doc[skeys[jj]];;
		if (s !== "") {
		    s=s+" "+d
		} else {
		    s=d;
		}
	    }
	    return s;
	} else {
	    return element.cnt;
	}
    };
    this.getPriorityIndex=function(state,arr) {
	var len,ii;
	var pri={};
	len=arr.length;
	for (ii=0;ii<len;ii++) {
	    pri[arr[ii]]=0;
	};
	if (this.priority !== undefined) {
	    len=this.priority.length
	    for (ii=0;ii<len;ii++) {
		var key=this.priority[ii]
		pri[key]=len+1-ii
	    };
	};
	return pri;
    };
    this.changePriority=function(state,key,trg) {  // key -> trg
	if (key  === undefined || trg  === undefined) { return;}
	console.log("Priority:",key,"->",trg,JSON.stringify(this.priority));
	//if (typeof trg  === "undefined") {
	var col=state.Path.other.table[0];
	var row=state.Path.other.table[1];
	//var icol=0;
	//var irow=0;
	var ikey=0;
	var itrg=0;
	var len=this.priority.length;
	for (var ii=0;ii<len;ii++) {
	    if (this.priority[ii]  === col) {
		//icol=len+1-ii;
	    };
	    if  (this.priority[ii]  === row) {
		//irow=len+1-ii;
	    };
	    if  (this.priority[ii]  === key) {
		ikey=len+1-ii;
	    }
	    if  (this.priority[ii]  === trg) {
		itrg=len+1-ii;
	    }
	}
	if (itrg < ikey) {        // demote existing key
	    if (itrg > 0) {       // key exists on priority list
		state.Utils.spliceArray(this.priority,len+2-itrg,0,key);  // add after

		console.log("Added:",JSON.stringify(this.priority),ikey,itrg,key);

		//var src=
		this.priority.splice(len+1-ikey, 1);        // remove
	    } else {              // key exists, target does not
		//var src=
		this.priority.splice(len+1-ikey, 1);        // remove
		this.priority.concat(key)	    
	    }
	} else if (itrg > ikey) { // demote, key may not exist on priority list
	    if (ikey>0) {         // key exists on priority list
		//var src=
		this.priority.splice(len+1-ikey, 1);        // remove
		state.Utils.spliceArray(this.priority,len+1-itrg,0,key);  // add before
	    } else {              // key is not on priority list
		state.Utils.spliceArray(this.priority,len+1-itrg,0,key);  // add before
	    }
	} else if (itrg === 0) { // key and target not on the priority list
	    this.priority.concat(key)
	}
	console.log("Changed priority:",JSON.stringify(this.priority),ikey,itrg);
	return true;
    }
    //this.flipTable=function(state) {
    //    var bb=this.colrow[0];
    //    this.colrow[0]=this.colrow[1];
    //    this.colrow[1]=bb;
    //    //console.log("Setup:",JSON.stringify(setup));
    //};
    this.getPriority=function(state) {
	return state.Utils.cp(this.priority);
    };
    this.setPriority=function(state,priority) {
	this.priority=priority;
    }
};
export default Layout;
