import React, {Component} from "react";
import {withStyles} from "@material-ui/core";
import PropTypes from  "prop-types";
import Path      from  "./path/PathComponent";
import Map       from  "./MapComponent";
import Table0D   from  "./Table0DComponent";
import Table1D   from  "./Table1DComponent";
import Table2D   from  "./Table2DComponent";
import Circular from './Circular';

//console.log("Inside Dataset.")

const styles = theme => ({
    body: {
        margin: theme.spacing.getMaxWidth.margin,
	width: '90%',
	height: '90%',
    },
    table: {
        textAlign: 'left',
        padding: theme.spacing.unit * 0,
	height: '100%',
	width: '100%',
	borderStyle: 'solid',
    },
    row  :{alignItems: "stretch"},
    cell : {cursor: "pointer"},
});
//        maxWidth: theme.spacing.getMaxWidth.maxWidth,

function Switcher(props) {
    const { state, progress } = props;
    //var skeys=state.Matrix.sortedKeys(state,state.Matrix.keyCnt);
    var dim        = state.Show.getDim(state)
    var mode       = state.Show.getLayoutMode(props.state);
    //console.log(">>>>>> Switcher Dim:",dim," mode:",mode);
    if (progress) { // processing
	return (<div style={{width:'100%',margin:'0 auto'}}>
	          <Circular color={color}/>
	       </div>);
    } else if (mode === state.Show.code.layout.Map) {
	return (<Map state={state}/>);
    } else if (dim===2) {
	return (<Table2D state={state}/>);
    } else if (dim===1) {
	return (<Table1D state={state}/>);
    } else if (dim===0) {
	return (<Table0D state={state}/>);
    }
};

class Dataset extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Dataset=this;
	this.state={progress:false};
    };
    showMatrix(state,matrix) {
	state.React.matrix=matrix;
	//console.log("Datacomponent matrix:",JSON.stringify(state.React.matrix));
	this.forceUpdate();
    };
    setProgress(state,active) {
	//console.log("Setlog...",this.state.color," -> ",color);
	this.setState({progress:active});
	//this.forceUpdate();
    };
    render() {
        const { classes, state } = this.props;
        return (
            <div className={classes.body}>
                <Path     state={state}/>
		<Switcher state={state} progress={this.state.progress}/>
            </div>
        );
    }

}

Dataset.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Dataset);
